package cn.edu.hdu.acm.problem1063;import java.math.BigDecimal;import java.io.BufferedInputStream;import java.util.Scanner;public class Main {    public static void main(String[] args) {        Scanner input = new Scanner(new BufferedInputStream(System.in));        while(input.hasNext()){            BigDecimal a=input.nextBigDecimal();            int b=input.nextInt();            BigDecimal k = a.pow(b);            String s=k.stripTrailingZeros().toPlainString();            if(s.startsWith("0.")){                System.out.println(s.substring(1));            }            else {                System.out.println(s);            }        }    }}