package cn.edu.hdu.acm.problem1251;import java.io.BufferedInputStream;import java.util.Scanner;public class Main {    public static void main(String[] args)  {        Scanner input = new Scanner(new BufferedInputStream(System.in));        Trie trie = new Trie();        while(input.hasNextLine()) {            String word = input.nextLine();            if (word.equals("")) {                break;            }            trie.insert(word);        }        while(input.hasNext()) {            String prefix = input.next();            System.out.println(trie.countPrefix(prefix));        }    }}